document.addEventListener("DOMContentLoaded",(function(){let e=document.querySelectorAll(".questions__head"),t=document.querySelectorAll(".questions__body");e.forEach((e=>{e.addEventListener("click",(()=>{t.forEach((t=>{t.style.maxHeight?(t.style.maxHeight=null,t.style.paddingTop="0px",t.previousElementSibling.classList.remove("active")):e.dataset.tab===t.dataset.tab&&(t.previousElementSibling.classList.add("active"),t.style.maxHeight=t.scrollHeight+20+"px",t.style.paddingTop="20px")}))}))}))}));